mapper.select:
select * from(select rownum rnum, a.* from (select theater.theater_code, theater.theater_name, theater.area_code, theater.theater_address, area.area_name from tbl_theater theater, tbl_area area where theater.area_code = area.area_code
and area.area_code = #{area_code}
and theater.theater_state = 'Y' order by area_code desc) a) where rnum between #{start_row} and #{end_row}

select * from(select rownum rnum, a.* from (select theater.theater_code, theater.theater_name, theater.area_code, theater.theater_address, theater.theater_del_date, area.area_name from tbl_theater theater, tbl_area area where theater.area_code = area.area_code
and area.area_code = #{area_code}
and theater.theater_state = 'N' order by area_code desc) a) where rnum between #{start_row} and #{end_row}

select theater.theater_code, theater.theater_name, theater.area_code, theater.theater_address, area.area_name
from tbl_theater theater, tbl_area area
where theater.area_code = area.area_code and theater.theater_code = #{theater_code}

mapper.update:
update tbl_theater set
	theater_name = #{theater_name},
	area_code = #{area_code},
	theater_address = #{theater_address}
where theater_code = #{theater_code}

update tbl_theater set
    theater_state = 'Y'
where theater_code = #{theater_code}
